(function() {
  this.teaser = '<div class="teaserview">\n  {{body}}\n  {{meta.createdAt}}\n  {{> comments}}\n</div>';
  this.comments = '{{#comments}}\n  <div class="comment">\n    {{> author}}\n    {{body}}\n    {{meta.createdAt}}\n  </div>\n{{/comments}}';
  this.author = '<img src="{{avatar}}"><a href="#{{author.nickname}}">{{author.name}}</a>';
}).call(this);
